import React from "react";
import {useParams} from "react-router-dom";


class NoteForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            project_id: props?.notedata?.[0]?.id,
            text_of_note: ''
        }
        let {id} = useParams()
        console.log(id)
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value})
    }

    handleSubmit(event) {
        this.props.create_note(this.state.project_id, this.state.text_of_note)
        console.log(this.state.project_id, ' **** ', this.state.text_of_note)
        event.preventDefault()
    }

    render() {
        return (
            <form onSubmit={(event) => this.handleSubmit(event)}>
                <div className="form-group">
                    <label for="login">name</label>
                    <input type="text" className="form-control" name="text_of_note"
                           value={this.state.text_of_note}
                           onChange={(event) => this.handleChange(event)}/>
                </div>

                {/*<select name='authors' multiple onChange={(event) => this.handleChangeV2(event)}>*/}
                {/*    {this.props.authors.map((item) => <option value={item.id}> {item.first_name} </option>)}*/}
                {/*</select>*/}
                <input type="submit" className="btn btn-primary" value="Save"/></form>
        );
    }
}

export default NoteForm
